package org.vrjuggler.tweek.gui;

import java.util.EventListener;


public interface GlobalPrefsUpdateListener extends EventListener
{
   /**
    * Invoked when any part of the global preferences has been edited.
    */
   public void globalPrefsModified(GlobalPrefsUpdateEvent e);

   /**
    * Invoked when all the preferences are saved.  This does not imply that
    * any changes were made.
    */
   public void globalPrefsSaved(GlobalPrefsUpdateEvent e);
}
